import { IconButton, TextField } from '@material-ui/core';
import { Search } from '@material-ui/icons';
import React, { useState } from 'react'
import { connect } from 'react-redux';
import image from '../../photos/logo.svg'
import GroupIcon from '@material-ui/icons/Group';

import './style.css'

const CurrentBrandItem = (props) => {
  const [searchInput, setSearchInput] = useState('')

  const [switchCase, setSwitchCase] = useState(false)

    return(
      <div className='current_brand_position'>
        <div className='left-field'>
          <div className='search-panel'>
            <div className='search_input'>
                  <TextField label={'Найти товар...'} value={searchInput} onChange={(e) => setSearchInput(e.target.value)}/>
                  <div style={{marginTop: '20px'}}>
                      <Search/>
                  </div>
              </div>
          </div>
          <div className='items-field'>
            {
              switchCase ?
              <ul className='items-list'>
              {
                props.workers.filter(el => el.brand.id === props.currentBrand.id && el.full_name.toLowerCase().includes(searchInput.toLowerCase())).map(el =>
                  <li className='items-list-el'>
                    <p>
                      {el.full_name}
                    </p>
                    <p>
                      {el.worker_rank}
                    </p>
                    <p>
                      {el.birthdate}
                    </p>
                  </li>)
              }
            </ul>
            :
            <ul className='items-list'>
              {
                props.items.filter(el => el.brand.id === props.currentBrand.id && el.name.toLowerCase().includes(searchInput.toLowerCase())).map(el =>
                  <li className='items-list-el'>
                    <p>
                      {el.name}
                    </p>
                    <p>
                      {el.description}
                    </p>
                    <p>
                      {el.price}$
                    </p>
                  </li>)
              }
            </ul>
            }
          </div>
        </div>
        <div className='right-field'>
          <div className='right-field_label-position'>
            <h1 className='right-field_label'>{props.currentBrand.name}</h1>
            <IconButton onClick={() => setSwitchCase(!switchCase)}>
              <GroupIcon/>
            </IconButton>
          </div>
          <div className='right-field_image-position'>
            <img src={image} className='right-field_image'/>
          </div>
          <div className='right-field_description-position'>
            <p className='right-field_description'>{props.currentBrand.description}</p>
          </div>
        </div>
      </div>
    )
}

export default connect(
  state => ({
    currentBrand: state.brands.currentBrand,
    items: state.brands.items,
    workers: state.workers.workers,
  })
)(CurrentBrandItem);
