import { TextField } from '@material-ui/core';
import { Search } from '@material-ui/icons';
import React, { useState } from 'react'
import { connect } from 'react-redux';
import image from '../../photos/logo.svg'

const CurrentBrandItem = (props) => {
  const [searchInput, setSearchInput] = useState('')

    return(
      <div className='current_brand_position'>
        <div className='left-field'>
          <div className='search-panel'>
            <div className='search_input'>
                  <TextField label={'Найти бренд...'} value={searchInput} onChange={(e) => setSearchInput(e.target.value)}/>
                  <div style={{marginTop: '20px'}}>
                      <Search/>
                  </div>
              </div>
          </div>
          <div className='items-field'>
            <ul className='items-list'>

            </ul>
          </div>
        </div>
        <div className='right-field'>
          <div className='right-field_label-position'>
            <h1 className='right-field_label'>{props.currentBrand.name}</h1>
          </div>
          <div className='right-field_image-position'>
            <img src={image} className='right-field_image'/>
          </div>
          <div className='right-field_description-position'>
            <p className='right-field_description'>{props.currentBrand.description}</p>
          </div>
        </div>
      </div>
    )
}

export default connect(
  state => ({
    currentBrand: state.brands.currentBrand,
  })
)(CurrentBrandItem);
