import DateFnsUtils from '@date-io/date-fns'
import { IconButton } from '@material-ui/core'
import { Create, Delete } from '@material-ui/icons'
import { KeyboardDatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers'
import React, { useState } from 'react'
import { connect } from 'react-redux'
import { postDataElement } from '../../../main_redux/actions/server_connections'
import './style.css'

const WorkerItem = (props) => {
    console.log(props.currentWorker)
    const [date, setDate] = useState('')
    return(
        <div className='worker_item__position'>
            <div className='worker-info'>
                <p>
                    {props.currentWorker.brand.name}
                </p>
                <p>
                    {props.currentWorker.full_name}
                </p>
                <p>
                    {props.currentWorker.worker_rank}
                </p>
                <p>
                    {props.currentWorker.birthdate}
                </p>
            </div>
            <ul className='worker-work-days-list'>
                {
                    props.workDays.filter(el => el.worker.id === props.currentWorker.id).map(el =>
                        <li key={el.id} className='worker-work-days-el'>
                            <p>
                                {el.day}
                            </p>
                            <IconButton>
                                <Delete/>
                            </IconButton>
                        </li>
                    )
                }
            </ul>
            <div>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <KeyboardDatePicker
                    margin="normal"
                    id="date-picker-dialog"
                    format="MM/dd/yyyy"
                    value={date}
                    onChange={(e) => setDate(e)}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                    />
                </MuiPickersUtilsProvider>
                <IconButton>
                    <Create/>
                </IconButton>
            </div>

        </div>
    )
}

export default connect(
    state => ({
        currentWorker: state.workers.currentWorker,
        workDays: state.workers.workDays,
    }),
    dispatch => ({
        post: (obj, path, setter) => dispatch(postDataElement(obj, path, setter))
    })
)(WorkerItem)
