import React from 'react'
import './App.css';
import Header from './components/header';
import { Route, Switch } from 'react-router';
import BrandsList from './components/brands_list'
import Enter from './components/enter';
import BrandsItem from './components/brands_list/brands_item';
import ItemsList from './components/items_list';
import WorkerList from './components/worker_list'
import WorkerItem from './components/worker_list/worker_item';
import { ToastContainer, toast } from 'react-toastify';
import { connect } from 'react-redux';
import CurrentBrandItem from './components/current_brand_item';

function App(props) {
  console.log(props.currentWorker)
  return (
    <div className='app__position'>
       <ToastContainer
          position="top-left"
          autoClose={2000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover/>
      <div>
        <Header/>
      </div>
      <Switch>
        <Route exact path='/' component={Enter}/>
        <Route exact path='/brands_list' component={BrandsList}/>
        <Route path={`/brands_item_id=${props.currentBrand.id}`} component={CurrentBrandItem}/>
        <Route path={`/brands_item_id=${props.currentBrand.id}/worker_item_id=${1}`} component={WorkerItem}/>
      </Switch>
    </div>
  );
}

export default connect(
  state => ({
    currentBrand: state.brands.currentBrand,
    currentWorker: state.workers.currentWorker,
  })
)(App);
