import React from 'react'
import { connect } from 'react-redux'
import './style.css'

const WorkerItem = (props) => {
    console.log(props.currentWorker)
    return(
        <div className='worker_item__position'>
            <div className='worker-info'>
                <p>
                    {props.currentWorker.brand.name}
                </p>
                <p>
                    {props.currentWorker.full_name}
                </p>
                <p>
                    {props.currentWorker.worker_rank}
                </p>
                <p>
                    {props.currentWorker.birthdate}
                </p>
            </div>
            <ul>
                {
                    props.workDays.filter(el => el.worker.id === props.currentWorker.id).map(el =>
                        <li key={el.id}>
                            {el.day}
                        </li>
                    )
                }
            </ul>
        </div>
    )
}

export default connect(
    state => ({
        currentWorker: state.workers.currentWorker,
        workDays: state.workers.workDays,
    })
)(WorkerItem)
