import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { getData } from '../../main_redux/actions/server_connections'
import BrandsItem from './brands_item'
import { getBrands, getTradePavilions } from '../../main_redux/actions/brands'
import FilterPanel from './filter_panel'
import './style.css'


function BrandsList (props) {

    useEffect(() => {
        props.set('brands', getBrands);
      }, []);

    const [searchInput, setSearchInput] = useState('');
    const [currentBrand, setCurrentBrand] = useState('all')

    let filteredBrands = props.brands.filter(el => (el.name.toLowerCase().includes(searchInput.toLowerCase()) || searchInput === '') && (el.luxury_level === currentBrand || currentBrand === 'all'))

    console.log(props.brands)
    console.log(props.currentBrand)


    return(
        <div className='brands_list__position'>
            <div className='filter_panel'>
                <FilterPanel setSearchInput={setSearchInput} searchInput={searchInput} setCurrentBrand={setCurrentBrand}/>
            </div>
            <ul className='brands_list'>
                {
                    filteredBrands.map(el => (
                        <li key={el.id} className='brands_item'>
                            <BrandsItem el={el}/>
                        </li>
                    ))
                }
            </ul>
        </div>
    )
}
export default connect(
    state => ({
        brands: state.brands.brands,
        currentBrand: state.brands.currentBrand,
    }),
    dispatch => ({
        set: (path, setter) => dispatch(getData(path, setter))
    })
)(BrandsList)
