import { INDEX_ORDER, INDEX_ORDERED_DISH, SET_CURRENT_ORDER } from '../constants/orders'
import { toast } from 'react-toastify';

const initialState = {
  orders: [],
  orderedDishes: [],
  currentOrder: '',
  current_order_connect_status: false
}

const OrderReducer = (state = initialState, action) => {
  switch (action.type) {
    case INDEX_ORDER: {
      return {...state, orders: action.value}
    }
    case INDEX_ORDERED_DISH: {
      return {...state, orderedDishes: action.value}
    }
    case SET_CURRENT_ORDER: {
      if(action.message === 'create'){
        toast.success(`создан заказ №${action.value.id}`, {
          position: "top-left",
          autoClose: 2500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

        toast(`Стол №${action.value.table.number} занят`, {
            position: "top-left",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            });
      }
      if(action.message === 'update'){
        toast(`заказ №${action.value.id} дополняется`, {
          position: "top-left",
          autoClose: 2500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });
      }
      if(action.message === 'close'){
        toast.success(`заказ №${action.value.id} закрыт`, {
          position: "top-left",
          autoClose: 2500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

        toast.info(`Чек №${action.value.id} готов`, {
              position: "top-left",
              autoClose: 2500,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
              });

        toast(`Стол №${action.value.table.number} свободен`, {
                position: "top-left",
                autoClose: 2500,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
      }
      if(action.message === 'cancel'){
        toast.warning(`заказ №${action.value.id} отменен`, {
          position: "top-left",
          autoClose: 2500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

        toast(`Стол №${action.value.table.number} свободен`, {
            position: "top-left",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            });
      }
      return {...state, currentOrder: action.value, current_order_connect_status: true }
    }
    default:{
      return state;
    }
  }
}

export default OrderReducer
